import React, { useState, useEffect, createContext, useContext } from 'react';
import { 
  Menu, X, Phone, Mail, MapPin, Clock, Calendar, 
  Stethoscope, Heart, Activity, Users, Award, 
  ChevronRight, Star, ArrowRight, Facebook, 
  Instagram, Twitter, Linkedin 
} from 'lucide-react';
import { Link } from "react-router-dom";
// AuthContext
const AuthContext = createContext(null);

// Composant LoginRegister
function LoginRegister({ onClose }) {
  const { setUser } = useContext(AuthContext);
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    first_name: '',
    last_name: '',
    phone: '',
    date_of_birth: '1990-01-01',
    gender: 'Homme'
  });
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      if (isLogin) {
        // Connexion
        const response = await fetch('http://localhost:5001/api/auth/login', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            username: formData.email,
            password: formData.password
          })
        });

        const data = await response.json();

        if (data.success) {
          // Récupérer l'ID du patient correspondant
          const patientRes = await fetch(`http://localhost:5002/api/patients?search=${formData.email}`);
          const patientData = await patientRes.json();
          
          if (patientData.success && patientData.patients.length > 0) {
            setUser({ ...data.user, patient_id: patientData.patients[0].id });
          } else {
            setUser(data.user);
          }
          
          alert('Connexion réussie !');
          onClose();
        } else {
          setError(data.error || 'Identifiants incorrects');
        }
      } else {
        // Inscription - Créer d'abord le patient
        const patientResponse = await fetch('http://localhost:5002/api/patients', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            first_name: formData.first_name,
            last_name: formData.last_name,
            email: formData.email,
            phone: formData.phone,
            date_of_birth: formData.date_of_birth,
            gender: formData.gender
          })
        });

        const patientData = await patientResponse.json();

        if (patientData.success) {
          // Créer ensuite le compte utilisateur
          const authResponse = await fetch('http://localhost:5001/api/auth/register', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              username: formData.email,
              email: formData.email,
              password: formData.password,
              first_name: formData.first_name,
              last_name: formData.last_name,
              role: 'user'
            })
          });

          const authData = await authResponse.json();

          if (authData.success) {
            setUser({ ...authData.user, patient_id: patientData.patient.id });
            alert('Inscription réussie ! Vous pouvez maintenant prendre rendez-vous.');
            onClose();
          } else {
            setError(authData.error || 'Erreur lors de l\'inscription');
          }
        } else {
          setError(patientData.error || 'Erreur lors de la création du profil patient');
        }
      }
    } catch (err) {
      console.error(err);
      setError('Erreur de connexion au serveur');
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl p-8 max-w-md w-full relative max-h-[90vh] overflow-y-auto">
        <button 
          onClick={onClose}
          className="absolute top-4 right-4 text-gray-400 hover:text-gray-600"
        >
          <X className="w-6 h-6" />
        </button>

        <h2 className="text-2xl font-bold text-gray-900 mb-6">
          {isLogin ? 'Connexion' : 'Inscription'}
        </h2>

        {error && (
          <div className="mb-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded-lg">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-4">
          {!isLogin && (
            <>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Prénom</label>
                  <input
                    type="text"
                    value={formData.first_name}
                    onChange={(e) => setFormData({...formData, first_name: e.target.value})}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                    required={!isLogin}
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Nom</label>
                  <input
                    type="text"
                    value={formData.last_name}
                    onChange={(e) => setFormData({...formData, last_name: e.target.value})}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                    required={!isLogin}
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Téléphone</label>
                <input
                  type="tel"
                  value={formData.phone}
                  onChange={(e) => setFormData({...formData, phone: e.target.value})}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                  required={!isLogin}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Genre</label>
                <select
                  value={formData.gender}
                  onChange={(e) => setFormData({...formData, gender: e.target.value})}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                  required={!isLogin}
                >
                  <option value="Homme">Homme</option>
                  <option value="Femme">Femme</option>
                </select>
              </div>
            </>
          )}

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Email</label>
            <input
              type="email"
              value={formData.email}
              onChange={(e) => setFormData({...formData, email: e.target.value})}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Mot de passe</label>
            <input
              type="password"
              value={formData.password}
              onChange={(e) => setFormData({...formData, password: e.target.value})}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
              required
              minLength={8}
            />
            {!isLogin && (
              <p className="text-xs text-gray-500 mt-1">
                Min. 8 caractères avec majuscule, minuscule et chiffre
              </p>
            )}
          </div>

          <button
            type="submit"
            className="w-full bg-indigo-600 text-white py-3 rounded-lg font-semibold hover:bg-indigo-700 transition"
          >
            {isLogin ? 'Se connecter' : "S'inscrire"}
          </button>
        </form>

        <div className="mt-6 text-center">
          <button
            onClick={() => {
              setIsLogin(!isLogin);
              setError('');
            }}
            className="text-indigo-600 hover:text-indigo-700 font-medium"
          >
            {isLogin ? "Pas de compte ? S'inscrire" : 'Déjà un compte ? Se connecter'}
          </button>
        </div>
      </div>
    </div>
  );
}

// Provider d'authentification
function AuthProvider({ children }) {
  const [user, setUser] = useState(null);

  const setUserAndSave = (userData) => {
    setUser(userData);
  };

  return (
    <AuthContext.Provider value={{ user, setUser: setUserAndSave }}>
      {children}
    </AuthContext.Provider>
  );
}

function ClinicLandingPage() {
  const { user, setUser } = useContext(AuthContext);
  
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [activeSection, setActiveSection] = useState('accueil');
  const [showAuth, setShowAuth] = useState(false);
  const [doctors, setDoctors] = useState([]);
  const [loading, setLoading] = useState(true);

  const [appointmentForm, setAppointmentForm] = useState({
    patient_id: null,
    doctor_name: '',
    appointment_date: '',
    duration: 30,
    reason: '',
    notes: '',
    // Champs pour les non-connectés
    first_name: '',
    last_name: '',
    email: '',
    phone: '',
    date_of_birth: '1990-01-01',
    gender: 'Homme'
  });

  // Charger les médecins depuis le service
  useEffect(() => {
    const fetchDoctors = async () => {
      try {
        const response = await fetch('http://localhost:5006/api/doctors?is_active=true&per_page=4');
        const data = await response.json();
        if (data.success && data.doctors.length > 0) {
          setDoctors(data.doctors);
          setAppointmentForm(prev => ({ ...prev, doctor_name: `Dr. ${data.doctors[0].first_name} ${data.doctors[0].last_name}` }));
        } else {
          // Médecins par défaut si le service n'est pas disponible
          setDoctors([
            { id: 1, first_name: 'Sarah', last_name: 'Benali', specialization: 'Cardiologue', years_of_experience: 15 },
            { id: 2, first_name: 'Ahmed', last_name: 'Mansouri', specialization: 'Médecin Généraliste', years_of_experience: 12 },
            { id: 3, first_name: 'Leila', last_name: 'Kherroubi', specialization: 'Pédiatre', years_of_experience: 10 },
            { id: 4, first_name: 'Karim', last_name: 'Bouzid', specialization: 'Chirurgien', years_of_experience: 18 }
          ]);
          setAppointmentForm(prev => ({ ...prev, doctor_name: 'Dr. Sarah Benali' }));
        }
      } catch (error) {
        console.error('Erreur chargement médecins:', error);
        // Médecins par défaut
        setDoctors([
          { id: 1, first_name: 'Sarah', last_name: 'Benali', specialization: 'Cardiologue', years_of_experience: 15 },
          { id: 2, first_name: 'Ahmed', last_name: 'Mansouri', specialization: 'Médecin Généraliste', years_of_experience: 12 }
        ]);
        setAppointmentForm(prev => ({ ...prev, doctor_name: 'Dr. Sarah Benali' }));
      } finally {
        setLoading(false);
      }
    };

    fetchDoctors();
  }, []);

  useEffect(() => {
    if (user && user.patient_id) {
      setAppointmentForm(prev => ({ 
        ...prev, 
        patient_id: user.patient_id,
        first_name: user.first_name || '',
        last_name: user.last_name || '',
        email: user.email || '',
        phone: user.phone || ''
      }));
    } else {
      // Réinitialiser les champs si pas d'utilisateur
      setAppointmentForm(prev => ({ 
        ...prev, 
        patient_id: null,
        first_name: '',
        last_name: '',
        email: '',
        phone: ''
      }));
    }
  }, [user]);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
      setActiveSection(sectionId);
      setIsMenuOpen(false);
    }
  };

  const handleTakeAppointment = () => {
    scrollToSection('contact');
  };

  const handleLogout = () => {
    setUser(null);
    alert('Déconnexion réussie');
  };

  const handleAppointmentChange = (e) => {
    const { name, value } = e.target;
    setAppointmentForm({ ...appointmentForm, [name]: value });
  };

  const handleAppointmentSubmit = async (e) => {
    e.preventDefault();

    try {
      let patientId = user?.patient_id || appointmentForm.patient_id;

      // Si pas de patient_id, créer un nouveau patient
      if (!patientId) {
        // Validation des champs requis
        if (!appointmentForm.first_name || !appointmentForm.last_name || 
            !appointmentForm.email || !appointmentForm.phone) {
          alert('❌ Veuillez remplir tous les champs obligatoires (prénom, nom, email, téléphone)');
          return;
        }

        const patientPayload = {
          first_name: appointmentForm.first_name.trim(),
          last_name: appointmentForm.last_name.trim(),
          email: appointmentForm.email.trim(),
          phone: appointmentForm.phone.trim(),
          date_of_birth: appointmentForm.date_of_birth,
          gender: appointmentForm.gender
        };

        console.log('Création du patient avec:', patientPayload);

        const patientResponse = await fetch('http://localhost:5002/api/patients', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(patientPayload)
        });

        const patientData = await patientResponse.json();
        console.log('Réponse patient:', patientData);

        if (patientData.success) {
          patientId = patientData.patient.id;
        } else {
          alert('❌ ' + (patientData.error || 'Erreur lors de la création du profil patient'));
          return;
        }
      }

      // Validation de la date
      if (!appointmentForm.appointment_date) {
        alert('❌ Veuillez sélectionner une date et heure pour le rendez-vous');
        return;
      }

      // Formater la date pour l'API
      const formattedDate = appointmentForm.appointment_date.replace('T', ' ');

      const appointmentPayload = {
        patient_id: patientId,
        doctor_name: appointmentForm.doctor_name,
        appointment_date: formattedDate,
        duration: parseInt(appointmentForm.duration),
        reason: appointmentForm.reason.trim(),
        notes: appointmentForm.notes.trim(),
        // Ajouter les infos du patient pour l'API
        first_name: user?.first_name || appointmentForm.first_name.trim(),
        last_name: user?.last_name || appointmentForm.last_name.trim(),
        email: user?.email || appointmentForm.email.trim(),
        phone: user?.phone || appointmentForm.phone.trim()
      };

      console.log('Création du RDV avec:', appointmentPayload);

      // Créer le rendez-vous
      const response = await fetch('http://localhost:5003/api/appointments', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(appointmentPayload)
      });

      const data = await response.json();
      console.log('Réponse RDV:', data);

      if (data.success) {
        alert('✅ Rendez-vous créé avec succès ! Nous vous contacterons pour confirmation.');
        // Réinitialiser seulement les champs de RDV
        setAppointmentForm(prev => ({
          ...prev,
          appointment_date: '',
          reason: '',
          notes: '',
          // Garder les infos personnelles si non connecté
          ...(user ? {} : {
            first_name: '',
            last_name: '',
            email: '',
            phone: ''
          })
        }));
      } else {
        alert('❌ ' + (data.error || 'Erreur lors de la création du rendez-vous'));
      }

    } catch (error) {
      console.error('Erreur complète:', error);
      alert('❌ Erreur de connexion au serveur. Veuillez réessayer.');
    }
  };

  const services = [
    {
      icon: Stethoscope,
      title: "Consultation Générale",
      description: "Examens médicaux complets et diagnostics professionnels par nos médecins qualifiés.",
      color: "bg-blue-100 text-blue-600"
    },
    {
      icon: Heart,
      title: "Cardiologie",
      description: "Soins cardiaques spécialisés avec équipements de pointe pour votre santé cardiaque.",
      color: "bg-red-100 text-red-600"
    },
    {
      icon: Activity,
      title: "Médecine d'Urgence",
      description: "Service d'urgence disponible 24h/24 et 7j/7 pour tous vos besoins médicaux urgents.",
      color: "bg-green-100 text-green-600"
    },
    {
      icon: Users,
      title: "Pédiatrie",
      description: "Soins spécialisés pour les enfants avec une équipe attentionnée et expérimentée.",
      color: "bg-purple-100 text-purple-600"
    }
  ];

  const testimonials = [
    {
      name: "Fatima Zerrouki",
      text: "Excellente clinique avec un personnel très professionnel et attentionné. Je recommande vivement !",
      rating: 5
    },
    {
      name: "Mohamed Tlemcani",
      text: "Service rapide et efficace. Les médecins sont à l'écoute et prennent le temps d'expliquer.",
      rating: 5
    },
    {
      name: "Amina Bensalah",
      text: "Très satisfaite de la qualité des soins reçus. Équipe médicale compétente et bienveillante.",
      rating: 5
    }
  ];

  const stats = [
    { number: "15000+", label: "Patients Satisfaits" },
    { number: "25+", label: "Médecins Experts" },
    { number: "10+", label: "Années d'Expérience" },
    { number: "24/7", label: "Service d'Urgence" }
  ];

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
          <p className="mt-4 text-gray-600">Chargement...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white">
      {/* HEADER / NAVBAR */}
      <header className={`fixed top-0 w-full z-50 transition-all duration-300 ${
        scrolled ? 'bg-white shadow-lg' : 'bg-transparent'
      }`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-2">
              <div className="bg-indigo-600 p-2 rounded-lg">
                <Heart className="w-6 h-6 text-white" />
              </div>
              <div>
                <h1 className="text-xl font-bold text-gray-900">HealthCare+</h1>
                <p className="text-xs text-gray-500">Clinique Médicale</p>
              </div>
            </div>

            <nav className="hidden md:flex space-x-8 items-center">
              {['accueil', 'services', 'medecins', 'contact'].map((item) => (
                <button
                  key={item}
                  onClick={() => scrollToSection(item)}
                  className={`capitalize font-medium transition-colors ${
                    activeSection === item 
                      ? 'text-indigo-600' 
                      : scrolled ? 'text-gray-700 hover:text-indigo-600' : 'text-white hover:text-indigo-200'
                  }`}
                >
                  {item}
                </button>
              ))}

              {user ? (
                <div className="flex items-center space-x-4">
                  <span className="text-sm text-gray-700">
                    Bonjour, {user.first_name || user.username}
                  </span>
                  <button
                    onClick={handleLogout}
                    className="px-4 py-2 bg-red-600 text-white rounded-full hover:bg-red-700 transition text-sm"
                  >
                    Déconnexion
                  </button>
                </div>
              ) : (
                <button
                  onClick={() => setShowAuth(true)}
                  className="px-4 py-2 bg-gray-200 text-gray-700 rounded-full hover:bg-gray-300 transition"
                >
                  Connexion
                </button>
              )}
            </nav>

            <button 
              onClick={handleTakeAppointment} 
              className="hidden md:flex items-center px-6 py-2 bg-indigo-600 text-white rounded-full hover:bg-indigo-700 transition"
            >
              <Calendar className="w-4 h-4 mr-2" />
              Prendre RDV
            </button>

            <button 
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="md:hidden p-2"
            >
              {isMenuOpen ? (
                <X className={scrolled ? 'text-gray-900' : 'text-white'} />
              ) : (
                <Menu className={scrolled ? 'text-gray-900' : 'text-white'} />
              )}
            </button>
          </div>
        </div>

        {isMenuOpen && (
          <div className="md:hidden bg-white border-t">
            <div className="px-4 py-4 space-y-3">
              {['accueil', 'services', 'medecins', 'contact'].map((item) => (
                <button
                  key={item}
                  onClick={() => scrollToSection(item)}
                  className="block w-full text-left px-4 py-2 text-gray-700 hover:bg-indigo-50 hover:text-indigo-600 rounded-lg capitalize"
                >
                  {item}
                </button>
              ))}
              {user ? (
                <>
                  <div className="px-4 py-2 text-sm text-gray-600">
                    Connecté: {user.first_name || user.username}
                  </div>
                  <button
                    onClick={handleLogout}
                    className="w-full px-4 py-2 bg-red-600 text-white rounded-lg"
                  >
                    Déconnexion
                  </button>
                </>
              ) : (
                <button
                  onClick={() => setShowAuth(true)}
                  className="w-full px-4 py-2 bg-gray-200 text-gray-700 rounded-lg"
                >
                  Connexion
                </button>
              )}
              <button 
                onClick={handleTakeAppointment} 
                className="w-full flex items-center justify-center px-6 py-3 bg-indigo-600 text-white rounded-full hover:bg-indigo-700"
              >
                <Calendar className="w-4 h-4 mr-2" />
                Prendre RDV
              </button>
            </div>
          </div>
        )}
      </header>

      {/* HERO SECTION */}
      <section id="accueil" className="relative pt-20 pb-32 bg-gradient-to-br from-indigo-600 via-indigo-700 to-purple-800 overflow-hidden">
        <div className="absolute inset-0 bg-black opacity-10"></div>

        <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 pt-20">
          <div className="grid md:grid-cols-2 gap-12 items-center">
            <div className="text-white space-y-6">
              <div className="inline-block px-4 py-2 bg-white bg-opacity-20 rounded-full backdrop-blur-sm">
                <span className="text-sm font-medium">🏥 Votre santé, notre priorité</span>
              </div>
              <h1 className="text-5xl md:text-6xl font-bold leading-tight">
                Des Soins de Qualité
                <span className="block text-indigo-200">Pour Votre Bien-être</span>
              </h1>
              <p className="text-xl text-indigo-100">
                Clinique moderne à Tlemcen offrant des services médicaux complets avec une équipe de professionnels dévoués disponible 24/7.
              </p>
              <div className="flex flex-wrap gap-4">
                <button 
                  onClick={handleTakeAppointment} 
                  className="flex items-center px-8 py-4 bg-white text-indigo-600 rounded-full font-semibold hover:bg-indigo-50 transition transform hover:scale-105"
                >
                  Prendre Rendez-vous
                  <ArrowRight className="w-5 h-5 ml-2" />
                </button>
                <button 
                  onClick={() => scrollToSection('services')}
                  className="flex items-center px-8 py-4 bg-transparent border-2 border-white text-white rounded-full font-semibold hover:bg-white hover:text-indigo-600 transition"
                >
                  Nos Services
                </button>
              </div>
            </div>

            <div className="hidden md:block">
              <div className="relative">
                <div className="bg-white rounded-2xl shadow-2xl p-8 transform rotate-3 hover:rotate-0 transition-transform duration-300">
                  <img 
                    src="https://images.unsplash.com/photo-1631217868264-e5b90bb7e133?w=600" 
                    alt="Clinique" 
                    className="rounded-xl w-full h-96 object-cover"
                  />
                </div>
              </div>
            </div>
          </div>

          <div className="grid grid-cols-2 md:grid-cols-4 gap-6 mt-20">
            {stats.map((stat, index) => (
              <div key={index} className="bg-white bg-opacity-10 backdrop-blur-md rounded-xl p-6 text-center border border-white border-opacity-20">
                <h3 className="text-4xl font-bold text-white mb-2">{stat.number}</h3>
                <p className="text-indigo-100">{stat.label}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* SERVICES SECTION */}
      <section id="services" className="py-20 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <span className="text-indigo-600 font-semibold text-sm uppercase tracking-wide">Nos Services</span>
            <h2 className="text-4xl font-bold text-gray-900 mt-2">Services Médicaux Complets</h2>
            <p className="text-xl text-gray-600 mt-4 max-w-3xl mx-auto">
              Nous offrons une gamme complète de services médicaux avec des équipements modernes et une équipe expérimentée
            </p>
          </div>

          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8">
            {services.map((service, index) => (
              <div 
                key={index} 
                className="bg-white rounded-xl p-6 shadow-lg hover:shadow-2xl transition-all duration-300 transform hover:-translate-y-2 cursor-pointer group"
              >
                <div className={`${service.color} w-16 h-16 rounded-lg flex items-center justify-center mb-4 group-hover:scale-110 transition-transform`}>
                  <service.icon className="w-8 h-8" />
                </div>
                <h3 className="text-xl font-bold text-gray-900 mb-2">{service.title}</h3>
                <p className="text-gray-600 mb-4">{service.description}</p>
                <button className="flex items-center text-indigo-600 font-semibold hover:gap-2 transition-all">
                  En savoir plus
                  <ChevronRight className="w-5 h-5 ml-1" />
                </button>
              </div>
            ))}
          </div>

          <div className="mt-16 bg-white rounded-2xl shadow-xl overflow-hidden">
            <div className="grid md:grid-cols-3 gap-8 p-8">
              <div className="text-center">
                <div className="bg-green-100 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4">
                  <Clock className="w-8 h-8 text-green-600" />
                </div>
                <h4 className="text-lg font-bold text-gray-900 mb-2">Horaires Flexibles</h4>
                <p className="text-gray-600">Ouvert 7j/7 de 8h à 20h</p>
              </div>
              <div className="text-center">
                <div className="bg-blue-100 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4">
                  <Award className="w-8 h-8 text-blue-600" />
                </div>
                <h4 className="text-lg font-bold text-gray-900 mb-2">Équipe Certifiée</h4>
                <p className="text-gray-600">Médecins diplômés et expérimentés</p>
              </div>
              <div className="text-center">
                <div className="bg-purple-100 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4">
                  <Heart className="w-8 h-8 text-purple-600" />
                </div>
                <h4 className="text-lg font-bold text-gray-900 mb-2">Soins Personnalisés</h4>
                <p className="text-gray-600">Approche centrée sur le patient pour un suivi optimal</p>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* MEDECINS SECTION */}
      <section id="medecins" className="py-20 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <span className="text-indigo-600 font-semibold text-sm uppercase tracking-wide">Nos Médecins</span>
            <h2 className="text-4xl font-bold text-gray-900 mt-2">Experts Qualifiés</h2>
            <p className="text-xl text-gray-600 mt-4 max-w-3xl mx-auto">
              Rencontrez notre équipe médicale spécialisée et dévouée à votre santé.
            </p>
          </div>

          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8">
            {doctors.map((doc) => (
              <div key={doc.id} className="bg-gray-50 rounded-xl p-6 shadow hover:shadow-2xl transition-all">
                <div className="bg-indigo-100 w-20 h-20 rounded-full flex items-center justify-center mx-auto mb-4">
                  <Stethoscope className="w-10 h-10 text-indigo-600" />
                </div>
                <h3 className="text-xl font-bold text-gray-900 mb-2">{`Dr. ${doc.first_name} ${doc.last_name}`}</h3>
                <p className="text-gray-600 mb-2">{doc.specialization}</p>
                <p className="text-gray-500 text-sm">{doc.years_of_experience} ans d'expérience</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* CONTACT / APPOINTMENT SECTION */}
      <section id="contact" className="py-20 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-12">
            <span className="text-indigo-600 font-semibold text-sm uppercase tracking-wide">Prendre Rendez-vous</span>
            <h2 className="text-4xl font-bold text-gray-900 mt-2">Réservez Votre Consultation</h2>
            <p className="text-xl text-gray-600 mt-4 max-w-3xl mx-auto">
              Remplissez le formulaire ci-dessous pour planifier un rendez-vous avec nos médecins.
            </p>
          </div>

          <div className="max-w-2xl mx-auto bg-white rounded-2xl shadow-lg p-8">
            {!user && (
              <div className="bg-blue-50 border-l-4 border-blue-500 p-4 mb-6">
                <p className="text-sm text-blue-800">
                  ℹ️ <strong>Nouveau patient :</strong> Veuillez remplir vos informations pour prendre rendez-vous
                </p>
              </div>
            )}
            
            <form onSubmit={handleAppointmentSubmit} className="space-y-4">
              {!user && (
                <div className="bg-indigo-50 border border-indigo-200 rounded-lg p-4 mb-6">
                  <p className="text-sm text-indigo-800 mb-4">
                    <strong>📋 Vos informations personnelles</strong>
                  </p>
                  
                  <div className="grid grid-cols-2 gap-4 mb-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Prénom *</label>
                      <input
                        type="text"
                        name="first_name"
                        value={appointmentForm.first_name}
                        onChange={handleAppointmentChange}
                        className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                        required
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Nom *</label>
                      <input
                        type="text"
                        name="last_name"
                        value={appointmentForm.last_name}
                        onChange={handleAppointmentChange}
                        className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                        required
                      />
                    </div>
                  </div>

                  <div className="grid grid-cols-2 gap-4 mb-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Email *</label>
                      <input
                        type="email"
                        name="email"
                        value={appointmentForm.email}
                        onChange={handleAppointmentChange}
                        className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                        required
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Téléphone *</label>
                      <input
                        type="tel"
                        name="phone"
                        value={appointmentForm.phone}
                        onChange={handleAppointmentChange}
                        className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                        required
                      />
                    </div>
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Date de naissance *</label>
                      <input
                        type="date"
                        name="date_of_birth"
                        value={appointmentForm.date_of_birth}
                        onChange={handleAppointmentChange}
                        className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                        required
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Genre *</label>
                      <select
                        name="gender"
                        value={appointmentForm.gender}
                        onChange={handleAppointmentChange}
                        className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                        required
                      >
                        <option value="Homme">Homme</option>
                        <option value="Femme">Femme</option>
                      </select>
                    </div>
                  </div>
                </div>
              )}

              <div className="bg-green-50 border border-green-200 rounded-lg p-4 mb-6">
                <p className="text-sm text-green-800 mb-4">
                  <strong>📅 Détails du rendez-vous</strong>
                </p>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Médecin *</label>
                  <select
                    name="doctor_name"
                    value={appointmentForm.doctor_name}
                    onChange={handleAppointmentChange}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                    required
                  >
                    {doctors.map((doc) => (
                      <option key={doc.id} value={`Dr. ${doc.first_name} ${doc.last_name}`}>
                        {`Dr. ${doc.first_name} ${doc.last_name}`} - {doc.specialization}
                      </option>
                    ))}
                  </select>
                </div>

                <div className="mt-4">
                  <label className="block text-sm font-medium text-gray-700 mb-2">Date & Heure *</label>
                  <input
                    type="datetime-local"
                    name="appointment_date"
                    value={appointmentForm.appointment_date}
                    onChange={handleAppointmentChange}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                    required
                  />
                </div>

                <div className="mt-4">
                  <label className="block text-sm font-medium text-gray-700 mb-2">Durée (minutes) *</label>
                  <input
                    type="number"
                    name="duration"
                    value={appointmentForm.duration}
                    onChange={handleAppointmentChange}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                    required
                    min={15}
                    max={120}
                  />
                </div>

                <div className="mt-4">
                  <label className="block text-sm font-medium text-gray-700 mb-2">Motif *</label>
                  <input
                    type="text"
                    name="reason"
                    value={appointmentForm.reason}
                    onChange={handleAppointmentChange}
                    placeholder="Ex: Consultation générale, suivi, etc."
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                    required
                  />
                </div>

                <div className="mt-4">
                  <label className="block text-sm font-medium text-gray-700 mb-2">Notes (optionnel)</label>
                  <textarea
                    name="notes"
                    value={appointmentForm.notes}
                    onChange={handleAppointmentChange}
                    placeholder="Informations complémentaires..."
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                    rows={3}
                  ></textarea>
                </div>
              </div>

              <button
                type="submit"
                className="w-full bg-indigo-600 text-white py-4 rounded-lg font-semibold hover:bg-indigo-700 transition flex items-center justify-center"
              >
                <Calendar className="w-5 h-5 mr-2" />
                Confirmer le Rendez-vous
              </button>

              {!user && (
                <p className="text-sm text-gray-500 text-center mt-4">
                  💡 Vous avez déjà un compte ?{' '}
                  <button
                    type="button"
                    onClick={() => setShowAuth(true)}
                    className="text-indigo-600 hover:text-indigo-700 font-medium"
                  >
                    Se connecter
                  </button>
                </p>
              )}
            </form>
          </div>
        </div>
      </section>

      {/* TESTIMONIALS SECTION */}
      <section className="py-20 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <span className="text-indigo-600 font-semibold text-sm uppercase tracking-wide">Témoignages</span>
            <h2 className="text-4xl font-bold text-gray-900 mt-2">Ce que disent nos patients</h2>
          </div>

          <div className="grid md:grid-cols-3 gap-8">
            {testimonials.map((t, idx) => (
              <div key={idx} className="bg-gray-50 rounded-2xl p-6 shadow hover:shadow-xl transition-all">
                <p className="text-gray-600 mb-4">"{t.text}"</p>
                <div className="flex items-center justify-between">
                  <span className="font-semibold text-gray-900">{t.name}</span>
                  <div className="flex space-x-1">
                    {Array.from({ length: t.rating }).map((_, i) => (
                      <Star key={i} className="w-4 h-4 text-yellow-400 fill-yellow-400" />
                    ))}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* FOOTER */}
      <footer className="bg-gray-900 text-gray-200 py-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 grid md:grid-cols-3 gap-8">
          <div>
            <h3 className="text-lg font-bold mb-4">HealthCare+</h3>
            <p>Clinique moderne offrant des soins de qualité avec une équipe expérimentée.</p>
            <div className="flex space-x-4 mt-4">
              <Facebook className="w-5 h-5 hover:text-indigo-400 transition cursor-pointer"/>
              <Twitter className="w-5 h-5 hover:text-indigo-400 transition cursor-pointer"/>
              <Instagram className="w-5 h-5 hover:text-indigo-400 transition cursor-pointer"/>
              <Linkedin className="w-5 h-5 hover:text-indigo-400 transition cursor-pointer"/>
            </div>
          </div>

          <div>
            <h4 className="font-semibold mb-4">Contact</h4>
            <p className="flex items-center mb-2"><Phone className="w-4 h-4 mr-2"/> +213 555 555 555</p>
            <p className="flex items-center mb-2"><Mail className="w-4 h-4 mr-2"/> contact@healthcare.com</p>
            <p className="flex items-center mb-2"><MapPin className="w-4 h-4 mr-2"/> Tlemcen, Algérie</p>
            <p className="flex items-center"><Clock className="w-4 h-4 mr-2"/> Lun - Dim: 8h - 20h</p>
          </div>

          <div>
            <h4 className="font-semibold mb-4">Liens Utiles</h4>
            <ul className="space-y-2">
              {['Accueil', 'Services', 'Médecins', 'Contact'].map((link, idx) => (
                <li key={idx}>
                  <button 
                    onClick={() => scrollToSection(link.toLowerCase())}
                    className="hover:text-indigo-400 transition flex items-center"
                  >
                    {link} <ChevronRight className="w-3 h-3 ml-1"/>
                  </button>
                </li>
              ))}
                      <li className="pt-4 border-t border-gray-800">
          <Link 
            to="/admin/login" 
            className="text-gray-500 hover:text-gray-400 text-xs transition"
          >
            Espace Administration
          </Link>
        </li>

            </ul>
          </div>
        </div>

        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 mt-8 pt-8 border-t border-gray-800 text-center text-gray-400 text-sm">
          <p>&copy; 2024 HealthCare+ Clinique. Tous droits réservés.</p>
        </div>
      </footer>

      
    </div>
  );
}

// APP ROOT
export default function App() {
  return (
    <AuthProvider>
      <ClinicLandingPage />
    </AuthProvider>
  );
}
